import React, {useEffect, useState} from 'react';
import Gmap from "./Gmap";
import Card from "@material-ui/core/Card";
import Moment from 'moment';
import {CardContent, Grid, Typography} from "@material-ui/core";
import {ForecastItems} from "./elementary/ForecastItems";
import {makeStyles} from '@material-ui/core/styles';
import {ForecastCard} from "./elementary/ForecastCard";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
}));

export function Forecast(props) {
    const classes = useStyles();
    const [forecastHours, setForecastHours] = useState('');

    console.log('props in forecast', props);

    const forecastItems = (props.hasFetched) ? (
        props.weather.daily.map((day, index) => <ForecastCard key={index + 1000} {...day}/>)
    ) : null;

    useEffect(()=> {
        if (props.hasFetched && props.forecastPeriod < 7) {

            setForecastHours(props.weather.hourly.map(forecast => {
                    if (Moment.unix(forecast.dt).format('DD') === Moment().add(props.forecastPeriod - 1, 'days').format('DD') &&
                        Moment.unix(forecast.dt).format('HH') % 6 === 0) {
                        return (
                            <Grid item key={forecast.dt}>
                                <Typography align='flex-start'>
                                    {Moment.unix(forecast.dt).format('HH:mm DD ')}
                                    {Math.floor(forecast.temp - 273.15, 1)}°C
                                    Wind {forecast.wind_speed} meters per second
                                </Typography>
                            </Grid>
                        )
                    }
                }) )
        }
    },[props])
    console.log('fired?',forecastHours)


    // let forecastHours = (props.hasFetched && props.forecastPeriod < 7) ? (
    //     props.weather.hourly.map(forecast => {
    //         if (Moment.unix(forecast.dt).format('DD') === Moment().add(props.weather.forecastPeriod - 1, 'days').format('DD') &&
    //             Moment.unix(forecast.dt).format('HH') % 6 === 0) {
    //             return (
    //                 <Grid item key={forecast.dt}>
    //                     <Typography align='flex-start'>
    //                         {Moment.unix(forecast.dt).format('HH:mm ')}
    //                         {Math.floor(forecast.temp - 273.15, 1)}°C
    //                         Wind {forecast.wind_speed} meters per second
    //                     </Typography>
    //                 </Grid>
    //             )
    //         }
    //     })
    // ) : null;

    const typeForecast = (props.hasFetched && props.forecastPeriod < 7) ? (
        <Grid item xs={4}>
            <Grid container spacing={3} direction='column' justify="space-between">
                <Grid item>
                    <Typography>
                        {Moment().add(props.forecastPeriod - 1, 'days').calendar(null, {
                            sameDay: '[Today]',
                            nextDay: '[Tomorrow]',
                            nextWeek: 'dddd',
                            sameElse: 'L'
                        })}
                    </Typography>
                </Grid>
                <Grid item>
                    <Typography>
                        {Moment().add(props.forecastPeriod - 1, 'days').format('MMMM, DD')}
                    </Typography>
                </Grid>
                <Grid item>
                    <Typography>
                        Time Weather
                    </Typography>
                </Grid>
                {forecastHours}
            </Grid>
        </Grid>
    ) : (
        <Grid container spacing={2} justify="flex-start">
            {(props.forecastPeriod < 7) ? null : forecastItems}
        </Grid>
    );

    return (
        <div>
            <Grid container spacing={2} wrap='wrap'
                  justify="space-between">
                {typeForecast}
                <Grid item xs={8}>
                    {(props.forecastPeriod < 7) ? <Gmap {...props.actualLocation}/> : null}
                </Grid>
            </Grid>
        </div>
    );
}
