import React from 'react';
import Card from "@material-ui/core/Card";
import Moment from 'moment';
import {CardContent, Grid, Typography} from "@material-ui/core";

export function Forecast(props) {
    console.log('props in forecast', props)

  const forecastCards = props.list.map( (day, index) => {
        return (
            <Grid item key={ (index+1)*1000}>
            <Card>
            <CardContent>
                <Typography align='center' variant='subtitle2'>{Moment().add(index+1, 'days').format('ddd, MMM DD')}</Typography>
                <Typography align='center' variant='subtitle2'>{Math.floor(day.main.temp_max - 273.15, 1)}/{Math.floor(day.main.temp_min - 273.15, 1)} C</Typography>
                <Typography align='center' variant='caption'>Wind {day.wind.speed} m/s</Typography>
            </CardContent>
            </Card>
            </Grid>
  )
    })

    return (
        <div>
            <Grid container spacing={2} wrap="wrap" justify='center'>
            {forecastCards}
            </Grid>
        </div>
    );
};
