import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from "react-redux";
import {getCurrentWeather} from '../app/appThunk'
import { makeStyles } from '@material-ui/core/styles';
import Container from "@material-ui/core/Container";
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
        root: {
                display: 'flex',
                flexWrap: 'wrap',
                '& > *': {
                        margin: theme.spacing(1),
                        width: theme.spacing(16),
                        height: theme.spacing(16),
                },
        },
}));

export default function Main() {
        const classes = useStyles();
        const dispatch = useDispatch();
        const weather = useSelector( store => store.weather);

        useEffect(()=> {
                if (!weather.hasFetched && !weather.isFetchingError && !weather.isFetching) {
                        dispatch(getCurrentWeather())
                }
        }, [weather])

        console.log('weather in main component',weather.currentWeather)

        const Weather = (weather.hasFetched)?  (<div>
                    <h3>{weather.currentWeather.main.temp - 273.15} C</h3>

                    {weather.currentWeather.name}</div>)
         : null;


        return (
        <Container fixed>
                <Paper elevation={3}>
                        <Typography style={{ backgroundColor: '#cfe8fc', height: '30vh' }}>
                                {Weather}
                        </Typography>
                </Paper>
        </Container>
        );

}
