import {createAsyncThunk} from "@reduxjs/toolkit";
// import {getEndpointName} from "../endpoints";
import {fetchApi} from './request'

// export const getProjects = createAsyncThunk("projects/getProjects", () => {
//     return fetchApi(getEndpointName('projects'));
// });

const cors = 'https://cors-anywhere.herokuapp.com/'

export const getForecast = createAsyncThunk( "weather/getForecast", (arg) =>{
    const locArg = arg.location.city + ',' + arg.location.country_code2;
    const countDays = arg.days;
    return fetchApi(`${cors}http://api.openweathermap.org/data/2.5/forecast?q=${locArg}&appid=82d3d463b96e530d4b14b76571e81d3a`)
})

export const getCurrentLocation = createAsyncThunk("getCurrentLocation",() => {
    return fetchApi('https://api.ipgeolocation.io/ipgeo?apiKey=1dcb3c056a13421f85ff969ce829d0be')
})

export const getCurrentWeather = createAsyncThunk( "weather/getCurrentWeather", (location) =>{
    const locArg = location.city + ',' + location.country_code2
    return fetchApi(`${cors}http://api.openweathermap.org/data/2.5/weather?q=${locArg}&appid=82d3d463b96e530d4b14b76571e81d3a`)
})
