import React, {useEffect} from 'react';
import {useSelector, useDispatch} from "react-redux";
import {getCurrentLocation, getCurrentWeather} from './redux/appThunk'
import  Navbar from './components/Navbar'
import SavedCities from "./components/SavedCities";
import Main from "./components/Main";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
        // display: 'flex',
        // flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            // width: theme.spacing(16),
            // height: theme.spacing(16),
        },
    },
}));


function App() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const location = useSelector(store => store.location);
    const weather = useSelector(store => store.currentWeather);

    useEffect(()=> {
        if (!location.hasFetched && !location.isFetching) {
            dispatch(getCurrentLocation(location.actualLocation))
        }
    },[location]);

    useEffect(() => {
        if (!weather.hasFetched && !weather.isFetchingError && !weather.isFetching && !location.hasFetched) {
            dispatch(getCurrentWeather())
        }
    }, [weather, location]);


  return (
    <div className="App">
        <div className={classes.root}>
        <Navbar/>
        <Main/>
        <SavedCities/>
        </div>
    </div>
  );
}

export default App;
