import React from 'react';
import Card from "@material-ui/core/Card";
import Moment from 'moment';
import {CardContent, Grid, Typography} from "@material-ui/core";

export function Forecast(props) {
    console.log('props in forecast', props)

  const forecastCards = props.forecastWeather.list.map( (day, index) => {
        return (
            <Grid item >
            <Card>
            <CardContent>
                <Typography align='center' variant='h5'>{Moment().add(index+1, 'days').format('dddd')}</Typography>
                <Typography align='center' variant='h5'>{Math.floor(day.main.temp - 273.15, 1)} C</Typography>
                <Typography align='center' variant='h6'>Wind {day.wind.speed} meters per second</Typography>
            </CardContent>
            </Card>
            </Grid>
  )
    })

    return (
        <div>
            <Grid container spacing={2} wrap="wrap">
            {forecastCards}
            </Grid>
        </div>
    );
};
