import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from "react-redux";
import {getCurrentWeather} from '../redux/appThunk'
import {makeStyles} from '@material-ui/core/styles';
import Container from "@material-ui/core/Container";
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import {CardContent, Grid} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
}));

export default function Main() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const weather = useSelector(store => store.currentWeather);

    const Weather = (weather.hasFetched) ? (<Card>
        <CardContent justify="center">
            <h3>{Math.floor(weather.currentWeather.main.temp - 273.15, 1)} C</h3>
            <h2>{weather.currentWeather.name}, {weather.currentWeather.sys.country}</h2>
            <h4>Wind {weather.currentWeather.wind.speed} meter per second</h4>
        </CardContent>
        </Card>)
        : null;


    return (
        // <Container fixed>
            <Paper elevation={3}>
                <Grid container
                    justify="center"
                    alignItems="center">
                    {Weather}
                </Grid>
                {/*<div style={{backgroundColor: '#cfe8fc',*/}
                {/*    // height: '15vh'*/}
                {/*}}>*/}

                {/*</div>*/}
            </Paper>
        // </Container>
    );

}
