import React from 'react';
import Gmap from "./Gmap";
import Card from "@material-ui/core/Card";
import Moment from 'moment';
import {CardContent, Grid, Typography} from "@material-ui/core";
import {ForecastItems} from "./elementary/ForecastItems";
import { makeStyles } from '@material-ui/core/styles';
import {ForecastCard} from "./elementary/ForecastCard";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
}));

export function Forecast(props) {
    const classes = useStyles();

    // console.log('props in forecast', props);

    const forecastItems = (props.hasFetched) ? (
        props.weather.daily.map( (day, index) => <ForecastCard key={index + 1000} {...day}/>)
    ) : null;


       return (
        <div >
            <Grid container spacing={2} wrap='wrap'
                  justify="space-between" >

                <Grid item xs={3}>
                    <Grid container direction='column' justify="space-between" >
                        <Grid item>
                        <Typography>
                            {Moment().add(props.forecastPeriod - 1, 'days').calendar(null,{
                                sameDay : '[Today]',
                                nextDay : '[Tomorrow]',
                                nextWeek : 'dddd',
                                sameElse : 'L'
                            })}
                        </Typography>
                        </Grid>
                        <Grid item>
                            <Typography>
                                {Moment().add(props.forecastPeriod - 1, 'days').format('MMMM, DD')}
                            </Typography>
                        </Grid>
                    </Grid>
                </Grid>

                {/*<Grid container spacing={2} justify="flex-start">*/}
                {/*    {(props.forecastPeriod < 7) ? null : forecastItems }*/}
                {/*</Grid>*/}

                <Grid item xs={8}>
                    {(props.forecastPeriod < 7) ? <Gmap {...props.actualLocation}/> : null}
                </Grid>
            </Grid>
        </div>
    );
}
