import {createReducer} from "@reduxjs/toolkit";
import {getCurrentWeather, getForecast} from "./appThunk";



const initialState = {
  savedCities: [],
  actualCity: [],
  currentWeather: {},
  hasFetched: false,
  isFetching: false,
  isFetchingError: null
};

const appReducer = createReducer(initialState, {
  [getCurrentWeather.pending]: (state) => {
    console.log('getCurrentWeather.pending', state)
    state.isFetching = true;
    return state;
  },
  [getCurrentWeather.rejected]: (state, action) => {
    console.log('getCurrentWeather.pending', state)
    state.isFetching = false;
    state.error = action.error.message;
    return state;
  },
  [getCurrentWeather.fulfilled]: (state, action) => {
    state.isFetching = false;
    state.hasFetched = true;
    state.currentWeather = state.currentWeather.concat(action.payload);
    return state;
  }
})

export default appReducer;
