import {createReducer} from "@reduxjs/toolkit";
import {getCurrentLocation} from "../appThunk";



const initialState = {
    actualLocation: {},
    hasFetched: false,
    isFetching: false,
    isFetchingError: null
};

const locationReducer = createReducer(initialState, {
    [getCurrentLocation.pending]: (state) => {
        state.isFetching = true;
        return state;
    },
    [getCurrentLocation.rejected]: (state, action) => {
        state.isFetching = false;
        state.error = action.error.message;
        return state;
    },
    [getCurrentLocation.fulfilled]: (state, action) => {
        state.isFetching = false;
        state.hasFetched = true;
        state.actualLocation =  action.payload;
        return state;
    },
})

export default locationReducer;
