import {createAsyncThunk} from "@reduxjs/toolkit";
// import {getEndpointName} from "../endpoints";
import {fetchApi} from './request'

// export const getProjects = createAsyncThunk("projects/getProjects", () => {
//     return fetchApi(getEndpointName('projects'));
// });

export const getForecast = createAsyncThunk( "weather/getForecast", () =>{
    return fetchApi('api.openweathermap.org/data/2.5/forecast', 'get', {q:"Haifa,il", cnt: 7, appid:"82d3d463b96e530d4b14b76571e81d3a"})
})

export const getCurrentWeather = createAsyncThunk( "weather/getCurrentWeather", () =>{
    return fetchApi('api.openweathermap.org/data/2.5/weather', 'get', {q:"Haifa,il", appid:"82d3d463b96e530d4b14b76571e81d3a"})
})
