import React from 'react';
import Gmap from "./Gmap";
import Card from "@material-ui/core/Card";
import Moment from 'moment';
import {CardContent, Grid, Typography} from "@material-ui/core";
import {ForecastItems} from "./elementary/ForecastItems";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export function Forecast(props) {
    const classes = useStyles();

    // console.log('props in forecast', props);

    const forecastItems = (props.hasFetched) ? (
        <ForecastItems {...props}/>
    ) : null;


       return (
        <div className={classes.root}>
            <Grid container spacing={2} direction="column" wrap='wrap'
                  justify="flex-start"
                  alignItems="flex-start">

                <Grid container className={classes.root} >
                    {(props.forecastPeriod < 7) ? null : forecastItems }
                </Grid>

                {/*<Grid item xs={8}>*/}
                {/*    {(props.forecastPeriod < 7) ? <Gmap {...props.actualLocation}/> : null}*/}
                {/*</Grid>*/}
            </Grid>
        </div>
    );
}
